import React, { useState } from 'react';
import styled from 'styled-components';

const FormContainer = styled.form`
  display: flex;
  flex-direction: column;
  width: 300px;
  margin: 0 auto;
  padding: 20px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const FormLabel = styled.label`
  margin-bottom: 5px;
`;

const FormInput = styled.input`
  padding: 5px;
  margin-bottom: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const FormSubmit = styled.button`
  padding: 10px 20px;
  background-color: #0077cc;
  color: #fff;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #005fa3;
  }
`;

function Form() {
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [email, setEmail] = useState('');
  const [age, setAge] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log({ name, surname, email, age });
  };

  return (
    <FormContainer onSubmit={handleSubmit}>
      <FormLabel>
        Name:
        <FormInput type="text" value={name} onChange={(event) => setName(event.target.value)} />
      </FormLabel>
      <FormLabel>
        Surname:
        <FormInput type="text" value={surname} onChange={(event) => setSurname(event.target.value)} />
      </FormLabel>
      <FormLabel>
        Email:
        <FormInput type="email" value={email} onChange={(event) => setEmail(event.target.value)} />
      </FormLabel>
      <FormLabel>
        Age:
        <FormInput type="number" value={age} onChange={(event) => setAge(event.target.value)} />
      </FormLabel>
      <FormSubmit type="submit">Submit</FormSubmit>
    </FormContainer>
  );
}

export default Form;
