import React, { useState } from 'react';
import styled from 'styled-components';



function Form() {
    const [name, setName] = useState('');
    const [surname, setSurname] = useState('');
    const [email, setEmail] = useState('');
    const [age, setAge] = useState('');
    const [sex, setSex] = useState('male');

    const handleSubmit = (event) => {
      event.preventDefault();
      console.log({ name, surname, email, age, sex });
    };

    return (
      <form onSubmit={handleSubmit}>
        <Label>
        Name:
        <Input type="text" value={name} onChange={(event) => setName(event.target.value)} minLength={4} required />
      </Label>
      <Label>
        Surname:
        <Input type="text" value={surname} onChange={(event) => setSurname(event.target.value)} minLength={4} required />
      </Label>
        <label>
          Email:
          <input type="email" value={email} onChange={(event) => setEmail(event.target.value)} />
        </label>
        <label>
          Age:
          <input type="number" value={age} onChange={(event) => setAge(event.target.value)} />
        </label>
        <label>
          Sex:
          <select value={sex} onChange={(event) => setSex(event.target.value)}>
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </label>
        <button type="submit">Submit</button>
      </form>
    );
  }

export default Form;
